import React, { useEffect, useState } from 'react'

import NavBar from '../../Components/Navbar/NavBar'

import './Shop.css'

import { useParams } from 'react-router-dom'

import Axios from '../../Axios'

function Products() {
    const { id } = useParams();
    const [products, setproducts] = useState([])

    function addproduct(data) {
        Axios.put('cart/add-to-cart/' + data, data).then(res => {

        }
        )
    }
    useEffect(() => {
        console.log('s')
        Axios.get('/products/' + id).then((result) => {
            if (result.data.Products) {
                console.log(result.data.Products);
                setproducts(result.data.Products)

            } else {
                console.log('filed find products');
            }
        })
        return () => {
            console.log('unmount');
        }


    }, [id])
    return (
        <div className='shop-main'>
            <NavBar></NavBar>
            <div className='shop-container'>

                <div className='shop-1-helper'>
                    <p>heloo</p>

                </div>
                <div className='shop-2-products'>
                    {products.map((product) => {
                        return (
                            <div className='shop-2-1-product'>
                                <div className='shop-2-1-1-product'>
                                    <img src={product.image} alt='product'></img>
                                </div>
                                <div className='shop-2-1-2-product'>
                                    <h3>{product.name}</h3>
                                    <p>{product.description}</p>
                                    <p>{product.price}</p>
                                    <button onClick={() => addproduct(product)}>Add to cart</button>
                                </div>
                            </div>

                        )
                    })}

                </div>
            </div>
        </div>

    )
}

export default Products
