import React, { useEffect ,useState} from 'react'
import './Cart.css'
import { useSelector,useDispatch } from 'react-redux'
import { fetchCart } from '../../Features/Cart'
import Axios from '../../Axios'
import { resolve } from 'promise'
import Razorpay from 'react-razorpay';
import Navbar from '../../Components/Navbar/NavBar'


function Cart() {
    const dispatch = useDispatch()
    useEffect(() => {
       dispatch(fetchCart())
    },[])
    const cart = useSelector(state => state.cart)
    console.log(cart.cartInfo)
    var changeQuantity = (count,id) => {
        Axios.put('cart/change-product-quantity/' +id, { quantity: count, productID:id}).then(res => {
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    var removeCartProduct = id => {
        Axios.put('cart/remove-product/' + id,{id:id}).then(res => {
            console.log(res)
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    function deleteAll() {
        Axios.delete('cart/remove-all-products').then(res => {
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }


    return (
        <div className="cart-main">

        <Navbar></Navbar>
        <div className="cart-container">
                <div className="cart-products-info">
                    <div className="cart-info-tag">
                        <h1>FrShopping Cart</h1>
                        <h1>2Items</h1>
                    </div>
                    <div className="cart-info-list">
                        <table>
                            <thead>
                                <tr>
                                    <th className='text-initial cart-table-head-border'>Product Name</th>
                                    <th className='text-center cart-table-head-border'>Price</th>
                                    <th className='text-center cart-table-head-border'>Quantity</th>
                                    <th className='text-center cart-table-head-border'>Total</th>
                                    <th className='text-center cart-table-head-border'><button className='clear-cart'>Clear Cart</button></th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    cart.length >= 0 ? cart.cartInfo.map(item => {
                                        return (
                                            <tr key={item.id}>
                                                <td className='text-initial cart-table-body-border'>{item.name}</td>
                                                <td className='text-center cart-table-body-border'>{item.price}</td>
                                                <td className='text-center cart-table-body-border'>
                                                    <div className='cart-quantity-container'>
                                                        <button className='cart-quantity-button' onClick={() => changeQuantity(item.quantity - 1,item.id)}>-</button>
                                                        <span className='cart-quantity-text'>{item.quantity}</span>
                                                        <button className='cart-quantity-button' onClick={() => changeQuantity(item.quantity + 1,item.id)}>+</button>
                                                    </div>
                                                </td>
                                                <td className='text-center cart-table-body-border'>{item.price * item.quantity}</td>
                                                <td className='text-center cart-table-body-border'><button className='remove-cart-product' onClick={() => removeCartProduct(item.id)}>Remove</button></td>
                                            </tr>

                                        )
                                    }) : <tr><td colSpan='5' className='text-center'>No Products in Cart</td></tr>
                                }




                                {/* // cart.cartInfo.map(item => {
                                //         return (
                                //             <tr key={item.id}>
                                //                 <td className='text-initial cart-table-data-border'>{item.name}</td>
                                //                 <td className='text-center cart-table-data-border'>{item.price}</td>
                                //                 <td className='text-center cart-table-data-border'>
                                //                     <div className="cart-quantity">
                                //                         <button onClick={() => changeQuantity(item.quantity - 1,item.id)} className='cart-quantity-button'>-</button>
                                //                         <input type="text" value={item.quantity} className='cart-quantity-input'/>
                                //                         <button onClick={() => changeQuantity(item.quantity + 1,item.id)} className='cart-quantity-button'>+</button>
                                //                     </div>
                                //                 </td>
                                //                 <td className='text-center cart-table-data-border'>{item.total}</td>
                                //                 <td className='text-center cart-table-data-border'><button onClick={() => removeCartProduct(item.id)} className='cart-remove-button'>Remove</button></td>
                                //             </tr>
                                //         )
                                //     })
                                // } */} 
                            </tbody>
                        </table>
                    </div>


            </div>
            <div className="cart-billinfo">
                    <div className="cart-billinfo-header">
                        <h4>Bill Information</h4>
                    </div>
                    <div className="cart-billinfo-body">
                        <div className="cart-billinfo-body-item">
                            <button>Order Now </button>
                        </div>
                    </div>
            </div>
            </div>
        </div>
    )
}

export default Cart
