import React ,{useState}  from 'react'
import {Link } from 'react-router-dom'
import Axios from '../../Axios'
import './Signup.css'
function Signup() {
    const [name, setname] = useState('')
    const [number, setnumber] = useState('')
    const [password, setpassword] = useState('')
    // const [confirmpass, setconfirmpass] = useState('')
    const navigate = useNavigate()
    function sumbitform(e) {
        e.preventDefault() 
        Axios.post('/signup', { name, number, password }).then((result) => {
            if (result.data.status) {
                alert('user add to data base')
            } else {
                alert('something is wrong')
            }
        })
    }
    return (
        <div>
            <div className='main'>
                <div className="signup-bar">
                    <div className="image-logo">
                        {/* <img src={process.env.PUBLIC_URL + '/slpage.png'} alt="logo" /> */}
                    </div>
                    <div className='input-from'>
                        <form id="form-in">
                            <label>User Name</label>
                            <input type="text" value={name} onChange={(e)=>setname(e.target.value)} placeholder="enther a valid user name" />
                            <label>Phone</label>
                            <input type="number" value={number} onChange={(e)=>setnumber(e.target.value)} placeholder="enther phone"   />
                            <label>Password</label>
                            <input type="password" value={password} onChange={(e)=>setpassword(e.target.value)} placeholder="enther password" />
                            <label>Confirm Password</label>
                            <input type="password" placeholder="enther password" />
                            <Link to='/login'>already have an account</Link>
                            <button onClick={sumbitform}>Signup</button>

                        </form>

                    </div>
                </div>
            </div>
        </div>
    )
}

export default Signup
