import React, { useEffect, useState } from 'react'
import Axios, {ImagePath}from '../../Axios'
import {useParams} from 'react-router-dom'
function EditProduct() {
    const { id: idparams } = useParams()
    useEffect(() => {
        Axios.get(`/product/${idparams}`).then(res => {
            setproduct(res.data.product)

        })
    }, [idparams])
    const [product, setproduct] = useState({
        name:'',image:null,type:'',quantity:'',price:'', 
    })
    // const [name, setname] = useState('')
    // const [image, setimage] = useState(null)
    // const [type, settype] = useState('')
    // const [quantity, setquantity] = useState()
    // const [price, setprice] = useState()
    function addproduct() {
        const Data = new FormData();
        Data.append('name',product.name)
        Data.append('image', product.image);
        Data.append('type', product.type)
        Data.append('quantity', product.quantity)
        Data.append('price', product.price)
        Axios.post('/admin/addproduct', Data).then((result) => {
            console.log(result.data);
            result.data.status === true ? alert('Product Added Successfully') : alert(result.data.message)
        })
    }
    return (
        <div className='addproduct-main-div'>
            <div className="addproduct-box">
                <img src={ImagePath(imageId)}/>
                <input type="file" accept='.jpg' onChange={(e) => setproduct({ image: e.target.files[0]})} />
                <input type="text" value={product.name} onChange={(e) => setproduct({ name: e.target.value})} />
                <select value={product.type} onChange={(e) => setproduct({type: e.target.value})}>
                    <option value="">Select Product Type</option>
                    <option value="vegetables">Vegtable</option>
                    <option value="fruits">Fruits</option>
                    <option value="sweets">Sweets</option>
                </select>

                <input type="number" value={product.price}
                    onChange={(e) => setproduct({ price: e.target.value})}
                    placeholder="price"
                />

                <input type="number" value={product.quantity}
                    onChange={(e) => setproduct({ quantity: e.target.value})}
                    placeholder="quantity"
                />
                <button type='submit' onClick={(e) => addproduct()}>AddProduct</button>
            </div>
        </div>
    )
}

export default EditProduct
