import React, { useEffect, useState } from 'react'

import NavBar from '../../Components/Navbar/NavBar'

import './Shop.css'

import { useParams } from 'react-router-dom'

import Axios from '../../Axios'

function Products() {
    const { id } = useParams();
    const [products, setproducts] = useState([])

    function addproduct(data) {
        console.log(data)
        Axios.put('cart/add-to-cart/' + data, data).then(res => {

        }
        )
    }
    useEffect(() => {
        console.log('s')
        Axios.get('/products/' + id).then((result) => {
            if (result.data.Products) {
                console.log(result.data.Products);
                setproducts(result.data.Products)

            } else {
                console.log('filed find products');
            }
        })
        return () => {
            console.log('unmount');
        }


    }, [id])
    return (
        <div>
            <NavBar></NavBar>
            <div className='shop-container'>


                <div className='shop-1-helper'>

                </div>
                <div className='shop-2-products'>
                    {products.map((product) => {
                        return (
                            <div className='shop-2-item' key={product._id}>
                                <div className='shop-2-items-image'>
                                    <img src={product.image} />
                                </div>
                                <div className='shop-2-item-np'>
                                    <span>{product.name}</span>
                                    <span>{product.price}</span>
                                </div>
                                <div className='shop-2-item-add2cart'>
                                    <button onClick={() => addproduct(product)}>Add to cart</button>
                                </div>

                            </div>


                        )

                    })}

                </div>
            </div>
        </div>

    )
}

export default Products
