import React, { useEffect ,useState} from 'react'
import './Cart.css'
import { useSelector,useDispatch } from 'react-redux'
import { fetchCart } from '../../Features/Cart'
import Axios from '../../Axios'
import { resolve } from 'promise'
import Razorpay from 'react-razorpay';
import Navbar from '../../Components/Navbar/NavBar'

function loadRazorpay(src) {
    return new Promise(resolve => {
        const script = document.createElement("script");
        script.src = src
        document.body.appendChild(script);
        console.log(script)
        script.onload = () => {
            resolve(true);

        }
        script.onerror = () => {
            resolve(false);
        }
        document.body.appendChild(script);
    })
}

function Cart() {
   async  function displayRazor(Order) {
       const res = await loadRazorpay("https://checkout.razorpay.com/v1/checkout.js")
       if (!res) {
           alert("Razorpay is not loaded are you offline")
           return


       } else {
           console.log("Razorpay is loaded")

        var options = {
            "key": "rzp_test_lFLdi5y9B4LWvU", // Enter the Key ID generated from the Dashboard
            "amount": Order.amount, // Amount is in currency subunits. Default currency is INR. Hence, 50000 refers to 50000 paise
            "currency": "INR",
            "name": "Acme Corp",
            "description": "Test Transaction",
            "image": "https://example.com/your_logo",
            "order_id": Order.id,
            "handler": function (response) {
                Axios.post('order/verifypayment', { order: response }).then(res => {
                    if (res.data.status) {
                        alert("Payment Successfull")
                    } else {
                        alert("Payment Failed")
                    }
                })
            },
            "callback_url": "https://eneqd3r9zrjok.x.pipedream.net/",
            "prefill": {
                "name": "Gaurav Kumar",
                "email": "gaurav.kumar@example.com",
                "contact": "9999999999"
            },
            "notes": {
                "address": "Razorpay Corporate Office"
            },
            "theme": {
                "color": "#3399cc"
            }
        };

       const PaymentObject = new window.Razorpay(options);
       PaymentObject.open();
       }
   }

    const [name, setname] = useState('')
    const [number, setnumber] = useState('')
    const [address, setaddress] = useState('')
    const [city, setcity] = useState('')
    const [paymetType, setpaymetType] = useState()
    const dispatch = useDispatch()
    console.log(name,number,address,city,paymetType)
    useEffect(() => {
       dispatch(fetchCart())
    },[])

    const cart = useSelector(state => state.cart)
    console.log(cart.cartInfo)
    var changeQuantity = (count,id) => {
        Axios.put('cart/change-product-quantity/' +id, { quantity: count, productID:id}).then(res => {
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    var removeCartProduct = id => {
        Axios.put('cart/remove-product/' + id,{id:id}).then(res => {
            console.log(res)
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    function deleteAll() {
        Axios.delete('cart/remove-all-products').then(res => {
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    function OrderNow (e) {
        e.preventDefault()
        Axios.post('cart/place-order', { name: name, number: number, address: address, city: city, paymetType: paymetType }).then(res => {
            console.log(res)
            if (res.data.status) {
                displayRazor(res.data.order)


            }
        }).catch(err => {
            console.log(err)
        })
    }


    return (
        <div className="cart-main">


        <div className="cart-container">
            <Navbar></Navbar>
                <div className="cart-1-products">
                    <div className="cart-1-1-tag">
                        <h1>FrShopping Cart</h1>
                        <h1>2Prducts</h1>
                    </div>
                    <div className="cart-1-2-list">
                        <div className="cart-1-2-1-product">

                        </div>
                    </div>


            </div>
            <div className="cart-2-billinfo">

            </div>
            </div>
        </div>
    )
}

export default Cart
