import React, { useEffect } from 'react'
import './Cart.css'
import {  Link } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import { fetchCart,addToCart } from '../../Features/Cart'
import Axios, { ImagePath } from '../../Axios'


import Navbar from '../../Components/Navbar/NavBar'



function Cart() {
    const dispatch = useDispatch()
    useEffect(() => {
       dispatch(fetchCart())
    },[dispatch])

    const {cartInfo,error,loading} = useSelector(state => state.cart)
    var changeQuantity = (count, id) => {
        Axios.put('cart/change-product-quantity/' + id, { quantity: count, productID: id }).then(res => {
            if (res.data.status) {
                dispatch(fetchCart())
            }
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    var removeCartProduct = id => {
        Axios.put('cart/remove-product/' + id, { id: id }).then(res => {
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    function deleteAll() {
        Axios.delete('cart/remove-all-products').then(res => {
            dispatch(fetchCart())
        }).catch(err => {
            dispatch(fetchCart())
        })
    }
    return (
        <div>
            <Navbar></Navbar>
            <div className="cart-main">
                {
                    loading ? <div className="cart-loading">:)</div> :
                        error ? <div className="cart-error">{error}</div> :
                            cartInfo.length > 0 ?
                                <div className='cart-empty'>
                                    <h1 >Cart empty</h1>
                                </div> :
                                <h1>Cart empty</h1>



                }



            </div>
        </div>
    )

}

export default Cart
