        })
    }
    const deleteAll = id => {
        dispatch(removeAllProducts())
        Axios.delete('cart/remove-all-products').then(res => {
            if (res.data.status) {
            }
        })
    }
    return (
        <div>
            <Navbar></Navbar>
            <div className='cart-container'>
                <div className="cart-left">
                    <div className="cart-left-header">
                        <div className="cart-left-top-left">
                            <h3>My Cart</h3>
                            <p>You have {cartInfo.length} items in your cart</p>
                        </div>
                        <div className="cart-left-top-right">
                            <Link to='/shop'>
                                <button className='cart-left-top-right-button'>Continue Shopping</button>
                            </Link>
                        </div>
                    </div>

                </div>
                <div className="cart-right">

                </div>
            </div>

        </div>
    )

}

export default Cart
