import NavBar from "../../Components/Navbar/NavBar"
import './Order.css'
import { useSelector, useDispatch } from 'react-redux'

import { ImagePath } from '../../Axios'
import { useEffect } from "react"
import { fetchOrder } from '../../Features/Order'


function Order() {
    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(fetchOrder())
    }, [dispatch])
    const { OrderInfo } = useSelector(state => state.order)


    return (
        <div>
            <NavBar />
            <div className="order-container">
                <div className="ao">
                    <div className="ao-header">
                        <h2 className="ao-myorder">My Order</h2>
                    </div>
                    <div className="ao-body">
                        {
                            OrderInfo.map((order, index) => {
                                return (
                                    <div className='order-info' key={index}>
                                        <div className='oi-product'>
                                            {
                                                order.products.map((item, index) => {
                                                    return (
                                                        <img className='oi-images' src={ImagePath(item.imageId)} alt="" key={index} />

                                                    )
                                                }
                                                )
                                            }

                                        </div>
                                        <div className="oi-price">
                                            <span>â‚¹{order.totalPrice}</span>
                                        </div>
                                        <div className='oi-status'>
                                            <span>{order.OrderStatus}</span>
                                        </div>
                                        <div className='oi-paymet-type'>
                                            <span>{order.paymentType}</span>

                                        </div>
                                        <div className='oi-orderat'>
                                            <span>OrderAt:</span>
                                            <span>{order.OrderAt}</span>
                                        </div>
                                    </div>
                                )

                            })
                        }
                    </div>


                </div>
            </div>
        </div>
    )

}

export default Order
