import React, { useEffect } from 'react'
import './Cart.css'
import { useSelector,useDispatch } from 'react-redux'
import { fetchCart } from '../../Features/Cart'
import Axios from '../../Axios'
function Cart() {
    useEffect(() => {
       dispatch(fetchCart())
    }, [])
    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart)
    console.log(cart.cartInfo)
    var changeQuantity = count => {
        Axios.put('/changequantity/' + cart.cartInfo.id, {quantity: count})


    }

    return (
        <div>
            <div className='products' >
                <div className='items'>
                    {cart.status?'':<h1>cart.message</h1>}
                    {
                        cart.cartInfo.map(item => {
                            return (
                                <div className='item'>
                                    <img src='' alt='' />
                                    <div className='item-info'>
                                        <h3>{ item.name}</h3>
                                        <p>{ item.price}</p>
                                        <p>quantity={item.quantity}</p>
                                        <button onClick={changeQuantity(-1)}>-</button><input value={item.quantity} type="text" /><button onClick={changeQuantity(1)}>+</button>
                                    </div>
                                </div>

                            )
                        })
                    }
                </div>        
            </div>
            <div className='bill'>

            </div>
        </div>
    )
}

export default Cart
