import React from 'react';
import './Pop.css'
import { useNavigate } from 'react-router-dom'
import { AiOutlineCheckCircle, AiOutlineInfoCircle } from "react-icons/ai";

function Pop(props) {
    const navigate = useNavigate()
    const successfully = () => {
        props.setPop(false)
        navigate('/')
    }
    return (props.Pop.trigger) ? (
        <div onClick={() => props.setPop()}  className="pop-up">
            <div className='pop-up-content'>
                {
                    props.Pop.success ? <div className='pop-up-content-header'>
                        <AiOutlineCheckCircle color="green" size={57}  />
                    </div> :
                        <div className='pop-up-content-header'>
                            <AiOutlineInfoCircle color="red" size={57} />
                        </div>

                }
                <div className='pop-up-content-body'>
                    <span className='pop-up-message'>{props.Pop.message}</span>
                </div>

                <div className='pop-up-content-button'>
                    <button  onClick={()=>successfully()} className='pop-up-button'>Ok</button>
                </div>
            </div>        
        </div>
    ): "";
}
export default Pop;
