import React, { useEffect,useState} from 'react'

import axios from '../../Axios'
function AddProduct() {
    useEffect(() => {
        axios.get('admin').then(res => {
            if (res.data.status === false) {
                alert('Please Login')
                window.location.href = '/admin'
            }
        })
    }, )
    const [name, setname] = useState('')
    const [image, setimage] = useState(null)
    const [type, settype] = useState('')
    const [quantity, setquantity] = useState()
    const [price, setprice] = useState()

    function addproduct(e) {
        e.preventDefault();
        const Data = new FormData();
        Data.append('name', name)
        Data.append('image', image);
        Data.append('type', type)
        Data.append('quantity', quantity)
        Data.append('amount', price)
        axios.post('/admin/addproduct',Data).then((result) => {
            console.log(result.data);
            result.data.status === true ? alert('Product Added Successfully') : alert('Product Not Added chake agin')
        })

    }
    return (
        <div>
            <div>
                <input type="file" value={image} onChange={(e)=>setimage(e.target.files[0])} />
                <input type="text" value={name} onClick={(e) => setname(e.target.value)} />
                <select value={type} onChange={(e) => settype(e.target.value)}>
                    <option value="">Select Product Type</option>
                    <option value="vegtable">Vegtable</option>
                    <option value="fruis">Frutis</option>
                    <option value="sweets">Sweets</option>
                </select>
                <input type="number" value={price} onClick={(e) => setprice(e.target.value)} />
                <input type="number" value={quantity} onClick={(e)=>setquantity(e.target.value)} />
            </div>
        </div>
    )
}

export default AddProduct
