import React, { useEffect } from 'react'
import './Cart.css'
import { useSelector,useDispatch } from 'react-redux'
import {fetchCart} from '../../Features/Cart'
function Cart() {
    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart.cartInfo.cart)
    console.log(cart)
    useEffect(() => {
       dispatch(fetchCart())
    }, [])
    return (
        <div>
            <div className='products' >
                <div className='items'>
                    {
                        cart.map(item => {
                            return (
                                <div className='item' key={item.id}>
                                    <img src={item.image} alt={item.name} />
                                    <div className='item-info'>
                                        <h3>{item.name}</h3>
                                        <p>{item.price}</p>
                                        <p>{item.count}</p>
                                    </div>
                                </div>

                            )
                        })
                    }
                </div>        
            </div>
            <div className='bill'>

            </div>
        </div>
    )
}

export default Cart
