import { usedispatch } from 'react-redux'

import { removeFromCart, changeProductQuantity, removeAllProducts } from './Features/Cart'

const dispatch = useDispatch()
export const changeQuantity = (quantity, id) => {
    Axios.put(`cart/change-product-quantity/${id}`, { quantity }).then(res => {
        if (res.data.status) {
            dispatch(changeProductQuantity({ id, quantity }))
        }
    })
}
export const removeCartProduct = id => {
    Axios.put('cart/remove-product/' + id, { id: id }).then(res => {
        if (res.data.status) {
            dispatch(removeFromCart(id))
        }
    })
}
export const deleteAll = id => {
    Axios.delete('cart/remove-all-products').then(res => {
        if (res.data.status) {
            dispatch(removeAllProducts())
        }
    })
}
